import React, { useState } from 'react';
import HeaderComponent from '../components/header.component';
import { Switch, Route, Redirect } from 'react-router';
import yellow from '../components/images/logo3.png'
import blue from '../components/images/blue.png'
import red from '../components/images/charizard.png'
import gold from '../components/images/Gold2.png'



const UseraccountComponent: React.FC = () => {
    const [blueOwned, setBlueOwned] = useState('');
    
    const submit = (evt: any) =>{
        evt.preventDefault();
        let value = evt.target.getAttribute('data-value');
        console.log(value);
        let bOwned : boolean = false;

        if(value == 'blue'){
            if(bOwned){
                bOwned = false;
                console.log("You do not own Blue. Owned = " + bOwned);  
            }
            else{}
                bOwned = true;
                console.log("You do own Blue. Owned = " + bOwned);
            
        }
        
    }
    return (
        <div>
           <button type= "button" className="game_logo" id="blue"> 
           <img src= {blue} id="logo_pic" alt="game logo" onClick={submit} data-value = "blue"></img>
           </button> &nbsp;
           <button type= "button" className="game_logo" id="red"> 
           <img src= {red} id="logo_pic" alt="game logo" onClick={submit} data-value = "red"></img>
           </button> &nbsp;
           <button type= "button" className="game_logo" id="yellow"> 
           <img src= {yellow} id="logo_pic" alt="game logo" onClick={submit} data-value = "yellow"></img>
           </button> &nbsp;
           <button type= "button" className="game_logo" id="gold"> 
           <img src= {gold} id="logo_pic" alt="game logo" onClick={submit} data-value = "gold"></img> 
           </button> &nbsp;
           
            
        </div>
    )
}

export default UseraccountComponent;
