                else{
                    console.log("I do not own red");
                    setRedOwned(false);
                    console.log("Value of rOwned = " + redOwned);
                }
                break;
            case 'yellow':
                if(!yellowOwned){
                    console.log("I do own yellow");
                    setYellowOwned(true);
                    console.log("Value of yOwned = " + yellowOwned);
