                else{
                    console.log("I do not own yellow");
                    setBlueOwned(false);
                    console.log("Value of yOwned = " + yellowOwned);
                }
                break;
            default:
                break;
        }
        
    }
    return (
        <div>
           <button type= "button" className="game_logo" id="blue"> 
           <img src= {blue} id="logo_pic" alt="game logo" onClick={submit} data-value = "blue"></img>
           </button> &nbsp;
           <button type= "button" className="game_logo" id="red"> 
           <img src= {red} id="logo_pic" alt="game logo" onClick={submit} data-value = "red"></img>
           </button> &nbsp;
           <button type= "button" className="game_logo" id="yellow"> 
           <img src= {yellow} id="logo_pic" alt="game logo" onClick={submit} data-value = "yellow"></img>
           </button> &nbsp;
           <button type= "button" className="game_logo" id="gold"> 
           <img src= {gold} id="logo_pic" alt="game logo" onClick={submit} data-value = "gold"></img> 
           </button> &nbsp;
           
            
        </div>
    )
}

export default UseraccountComponent;
